mapper.select:
SELECT func_name AS func_id 
FROM m_base_func
WHERE del_flag = '0'


	AND func_id IN (
		SELECT DISTINCT mfp.func_id
		FROM m_func_power mfp
		WHERE del_flag = '0'			
		AND(
			power_id = #{userRoleId,jdbcType=VARCHAR}
			OR power_id = #{userId,jdbcType=VARCHAR}
		)	
		AND menu_id = #{menuId,jdbcType=VARCHAR}
		AND func_type = '1'		 
	)
