mapper.select:
SELECT count(*) FROM m_menu_power m LEFT JOIN m_menu mm ON mm.menu_id = m.menu_id and mm.del_flag='0' LEFT JOIN m_role mr ON mr.role_id = m.power_id and mr.del_flag='0' WHERE m.del_flag = '0'
AND mm.menu_name like CONCAT('%',#{menuKeyword,jdbcType=VARCHAR},'%' )
AND mr.role_name like CONCAT('%',#{powerId,jdbcType=VARCHAR},'%' )
AND mm.menu_pid = #{menuPid,jdbcType=VARCHAR}
AND mm.menu_id = #{menuId,jdbcType=VARCHAR}
AND m.power_type like CONCAT('%',#{powerType,jdbcType=VARCHAR},'%' )

SELECT m.id, m.power_type, ( CASE WHEN m.power_type = '00' THEN IFNULL((select mr.role_name from m_role mr where m.power_id = mr.role_id and mr.del_flag = '0'),m.power_id) ELSE IFNULL((select mu.user_name from m_user mu where m.power_id = mu.user_id and mu.del_flag = '0'),m.power_id) END )AS powerId, mm.menu_name, m.menu_id, m.remark, IFNULL((select mu.user_name from m_user mu where m.create_by = mu.user_id and mu.del_flag = '0'),m.create_by) as createBy, DATE_FORMAT(m.create_date,'%Y-%m-%d %H:%i:%S') AS create_date, IFNULL((select mu.user_name from m_user mu where m.update_by = mu.user_id and mu.del_flag = '0'),m.update_by) as updateBy, DATE_FORMAT(m.update_date,'%Y-%m-%d %H:%i:%S') AS update_date FROM m_menu_power m LEFT JOIN m_menu mm ON mm.menu_id = m.menu_id and mm.del_flag='0' LEFT JOIN m_role mr ON mr.role_id = m.power_id and mr.del_flag='0' WHERE m.del_flag = '0'
AND mm.menu_name like CONCAT('%',#{menuKeyword,jdbcType=VARCHAR},'%' )
and mr.role_name like CONCAT('%',#{powerId,jdbcType=VARCHAR},'%' )
and mm.menu_pid = #{menuPid,jdbcType=VARCHAR}
and mm.menu_id = #{menuId,jdbcType=VARCHAR}
and m.power_type like CONCAT('%',#{powerType,jdbcType=VARCHAR},'%' )
order by menu_id limit #{start},#{rows}
